import { Component } from "react";
import Cervice from "../cervice/Cervice";
import Item from "../item/Item";
import './ListItem.css'


class ListItem extends Component {
    constructor(props){
        super(props)
    }

    cervice = new Cervice

    state = {
        loading: true,
        items: []
    }

    onItemsLoaded = (newItems) => {
        this.setState(({items, loading}) => ({
            items: newItems,
            loading: false,
        }))
    }

    onRequest = () => {
        this.cervice
        .getAllItems()
        .then(res => this.onItemsLoaded(res))
    }

    componentDidMount(){
        this.onRequest()
    }

    createItems = (items) => {
        const elements = items.map(item => {
            return <Item key = {item.id} name = {item.name} imgShiny = {item.imgShiny} imgDef = {item.imgDef} />
        })
        return elements
    }

    render() {
        const {items, loading} = this.state
        console.log(items)
        return(
        <main> 
            <div className="container">
            {loading? null :  this.createItems(items)}
            </div>
        </main>    
        )
    }
}


export default ListItem
