class Cervice {

    getResource = async (url) => {
        let res = await fetch (url)

        if (!res.ok){
            throw new Error(`status: ${res.status}`)
        }

        return await res.json()
    }

    getAllItems = async () => {
        const res = await this.getResource(`https://pokeapi.co/api/v2/pokemon/?limit=4&offset=0/`)
        const urls = res.results.map(item => item.url)
        const items = urls.map(async (url) => await this.getResource(url))
        return await items

        // const res = await this.getResource(`https://pokeapi.co/api/v2/pokemon/?offset=0&limit=20`)
        // return res
    }
}


export default Cervice;
