import { Component } from "react";
import './Item.css'


class Item extends Component{
    constructor(props){
        super(props)
    }

    state = {
        img: this.props.imgDef,
        shiny: false,
    }

    onClick = () => {
        this.props.onClick(this.props.name)
    }


    onFocus = () => {
        var blink = setInterval (() => {
            let i = 0;
            this.setState({
                shiny: !this.state.shiny,
            })
            if (i === 20) clearInterval(this.blink)
        }, 50)
    }

    noFocus = () => {
        clearInterval(this.blink)
    }

    render (){
        const {name,id} = this.props
        const {img} = this.state
        const imgSkeleton = 'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/25.png'
        return (
            <div className="item" onClick={this.onClick} onMouseOver={this.onFocus} onMouseLeave={this.noFocus}>
                <img src={img? img : imgSkeleton} style={(id===404)? {'width': '100px', 'display': "block", 'margin': '20px auto'}: null}/>
                <h2 className="name">{name? name : 'Picatchu'}</h2>
            </div>
        )
    }
}

export default Item
