class Cervice {

    getResource = async (url) => {
        let res = await fetch (url)
        let _adress = `https://pokeapi.co/api/v2/`

        if (!res.ok){
            throw new Error(`status: ${res.status}`)
        }

        return await res.json()
    }

    getAllItems = async () => {
        const res = await this.getResource(`${this._adress}pokemon/?limit=4&offset=0/`)
        const urls = res.results.map(item => item.url)
        const items = await urls.map(async (url) => await this.getResource(url))
        return Promise.all(items)
    }

    transformItem = (item) => {
        // let description = await this.getResource(`${this._adress}characteristic/${item.id}`)
        let itemInfo = {
        name: item.name,
        // description: description,
        imgDefault: item.sprites.front_default,
        imgShiny: item.sprites.front_shiny,
        }

        return itemInfo
    }
}


export default Cervice;
