class Cervice {

    getResource = async (url) => {
        let res = await fetch (url)

        if (!res.ok){
            throw new Error(`status: ${res.status}`)
        }

        return await res.json()
    }

    getAllItems = async () => {
        const res = await this.getResource(`https://pokeapi.co/api/v2/pokemon/?limit=20&offset=0/`)
        const items = await res.results.map(async (item) => await this.getResource(item.url))
        .then((items) => console.log (items))

        return await items.json()
        // const res = await this.getResource(`https://pokeapi.co/api/v2/pokemon/?offset=0&limit=20`)
        // return res
    }
}


export default Cervice;
