import Cervice from '../cervice/Cervice';
import Header from '../header/Header';
import './App.css'
import logo from '../../images/pokemon-logo.svg'
import { Component } from 'react';
import ListItem from '../listItem/ListItem';
import ItemInfo from '../itemInfo/ItemInfo';


let cervice = new Cervice

class App extends Component {
constructor(props){
  super(props)
}

state = {
  name: 'BULBASAUR',
  img: 'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/25.pn',
  id: 1,
  description: `Loves to eat`,
  offset: '0',
}

cervice = new Cervice

onClick = (name) => {
  cervice.getItem(name)
  .then((item) => {
    console.log(item)
    this.setState({
      name: item.name,
      imgDef: item.imgDef,
      imgShiny: item.imgShiny,
      id: item.id,
      description: item.description,
    })
  }) 
}

  // componentDidMount = () => {
