import { Component } from "react";
import Cervice from "../cervice/Cervice";
import Item from "../item/Item";
import './ItemInfo.css'
import SearchPanel from "./searchPanel/SearchPanel";


class ItemInfo extends Component{
    constructor(props){
        super(props)
    }



    state = {
        id: this.props.id,
        name: this.props.name,
        imgShiny: this.props.imgShiny,
        imgDef: this.props.imgDef,
        description: this.props.description,
    }

    cervice = new Cervice

    onSubmit = (name) => {
        cervice.getItem(name)
        .then((item) => {
          this.setState({
            name: name,
            imgDef: item.imgDef,
            imgShiny: item.imgShiny,
            id: item.id,
            description: item.description,
          })
        })
    }

    render(){

        // const {img, name, loading} = this.state
        const {id, name, imgDef, imgShiny, description} = this.state
        // console.log()

        return(
            <aside className="itemInfo">
                 <div className="container">
                    <SearchPanel />
                    <Item key = {id} name = {name} imgShiny = {imgShiny} imgDef = {imgDef}/>
                </div>
             </aside>
        )
    }
}

export default ItemInfo
